#!/usr/bin/env node
var axios = require("axios")
var inquirer = require("inquirer")

inquirer.prompt(
    [
        {
            type:"input",
            name:"name",
            message:"请输入星座哦"
        }
    ]
).then(answers =>{
    axios.get(`http://web.juhe.cn:8080/constellation/getAll?key=1e86cbe4ac7c8e1d23dcc5a133221f7b 
    &type=today&consName=${answers.name}`).then(res=>{
        console.log(res)
    })
})